import React, { useState } from 'react';
import { CSVReader } from 'react-papaparse';
import { AlertCircle } from 'lucide-react';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';

const RandomWinnerSelector = () => {
  const [csvData, setCsvData] = useState([]);
  const [winners, setWinners] = useState({});
  const [error, setError] = useState('');

  const handleCSVUpload = (results) => {
    const data = results.data.slice(1); // Remove header row
    setCsvData(data);
    setError('');
  };

  const selectWinners = () => {
    if (csvData.length === 0) {
      setError('Please upload a CSV file first.');
      return;
    }

    const locationGroups = {};
    csvData.forEach((row) => {
      const location = row[4]; // Assuming Location is the 5th column (index 4)
      if (!locationGroups[location]) {
        locationGroups[location] = [];
      }
      locationGroups[location].push(row);
    });

    const selectedWinners = {};
    Object.keys(locationGroups).forEach((location) => {
      const group = locationGroups[location];
      const shuffled = group.sort(() => 0.5 - Math.random());
      selectedWinners[location] = shuffled.slice(0, 20);
    });

    setWinners(selectedWinners);
  };

  return (
    <div className="min-h-screen bg-gray-100 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-3xl mx-auto">
        <h1 className="text-3xl font-bold text-center mb-8">Random Winner Selector</h1>
        
        <div className="bg-white shadow-md rounded-lg p-6 mb-8">
          <h2 className="text-xl font-semibold mb-4">Upload CSV</h2>
          <CSVReader
            onDrop={handleCSVUpload}
            addRemoveButton
            onRemoveFile={() => setCsvData([])}
          >
            <span className="text-gray-600">Drop CSV file here or click to upload.</span>
          </CSVReader>
        </div>

        <button
          onClick={selectWinners}
          className="w-full bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded transition duration-300"
        >
          Select Winners
        </button>

        {error && (
          <Alert variant="destructive" className="mt-4">
            <AlertCircle className="h-4 w-4" />
            <AlertTitle>Error</AlertTitle>
            <AlertDescription>{error}</AlertDescription>
          </Alert>
        )}

        {Object.keys(winners).length > 0 && (
          <div className="mt-8">
            <h2 className="text-2xl font-bold mb-4">Winners</h2>
            {Object.entries(winners).map(([location, locationWinners]) => (
              <div key={location} className="mb-6">
                <h3 className="text-xl font-semibold mb-2">{location}</h3>
                <ul className="list-disc pl-5">
                  {locationWinners.map((winner, index) => (
                    <li key={index} className="mb-1">
                      {winner[3]} - {winner[2]}
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default RandomWinnerSelector;
