import React, { useState, useEffect } from 'react';
import { useCSVReader } from 'react-papaparse';

const RandomWinnerSelector = ({ backgroundImageUrl = 'logo.png' }) => {
  const [csvData, setCsvData] = useState([]);
  const [winners, setWinners] = useState({});
  const [error, setError] = useState('');
  const [isSpinning, setIsSpinning] = useState(false);
  const [currentSpin, setCurrentSpin] = useState({});
  const { CSVReader } = useCSVReader();

  const handleCSVUpload = (results) => {
    const data = results.data.slice(1); // Remove header row
    setCsvData(data);
    setError('');
  };

  const selectWinners = () => {
    if (csvData.length === 0) {
      setError('Please upload a CSV file first.');
      return;
    }

    setIsSpinning(true);
    const locationGroups = {};
    csvData.forEach((row) => {
      const location = row[4]; // Assuming Location is the 5th column (index 4)
      if (!locationGroups[location]) {
        locationGroups[location] = [];
      }
      locationGroups[location].push(row);
    });

    const spinDuration = 3000; // 3 seconds
    const spinInterval = 100; // Update every 100ms

    const spinTimer = setInterval(() => {
      const randomSpin = {};
      Object.keys(locationGroups).forEach(location => {
        const randomIndex = Math.floor(Math.random() * locationGroups[location].length);
        randomSpin[location] = locationGroups[location][randomIndex];
      });
      setCurrentSpin(randomSpin);
    }, spinInterval);

    setTimeout(() => {
      clearInterval(spinTimer);
      setIsSpinning(false);

      const selectedWinners = {};
      const usedNumbers = new Set();

      Object.keys(locationGroups).forEach((location) => {
        const group = locationGroups[location];
        const shuffled = group.sort(() => 0.5 - Math.random());
        selectedWinners[location] = [];

        for (const entry of shuffled) {
          const userNumber = entry[2]; // Assuming User_Number is the 3rd column (index 2)
          if (!usedNumbers.has(userNumber) && selectedWinners[location].length < 20) {
            selectedWinners[location].push(entry);
            usedNumbers.add(userNumber);
          }
          if (selectedWinners[location].length === 20) break;
        }
      });

      setWinners(selectedWinners);
    }, spinDuration);
  };

  return (
    <div className="min-h-screen bg-cover bg-center py-12 px-4 sm:px-6 lg:px-8" style={{backgroundImage: `url(${backgroundImageUrl})`}}>
      <div className="max-w-6xl mx-auto bg-white bg-opacity-90 p-8 rounded-lg shadow-xl">
        <h1 className="text-5xl font-bold text-center mb-8">Random Winner Selector</h1>
        
        <div className="mb-8">
          <h2 className="text-xl font-semibold mb-4">Upload CSV</h2>
          <CSVReader
            onUploadAccepted={handleCSVUpload}
            config={{
              skipEmptyLines: true,
            }}
          >
            {({ getRootProps, acceptedFile }) => (
              <>
                <div {...getRootProps()} className="border-2 border-dashed border-gray-300 p-4 text-center cursor-pointer">
                  {acceptedFile ? acceptedFile.name : 'Drop CSV file here or click to upload.'}
                </div>
              </>
            )}
          </CSVReader>
        </div>

        <button
          onClick={selectWinners}
          className="w-full bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-4 rounded transition duration-300"
        >
          Select Winners
        </button>

        {error && (
          <div className="mt-4 bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative" role="alert">
            <strong className="font-bold">Error:</strong>
            <span className="block sm:inline"> {error}</span>
          </div>
        )}

        {isSpinning && (
          <div className="mt-8 text-center">
            <h2 className="text-2xl font-bold mb-4">Selecting Winners...</h2>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
              {Object.entries(currentSpin).map(([location, winner]) => (
                <div key={location} className="bg-blue-100 p-4 rounded">
                  <h3 className="text-lg font-semibold mb-2">{location}</h3>
                  <p>{winner[3]} - {winner[2]}</p>
                </div>
              ))}
            </div>
          </div>
        )}

        {!isSpinning && Object.keys(winners).length > 0 && (
          <div className="mt-8">
            <h2 className="text-2xl font-bold mb-4">Winners</h2>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
              {Object.entries(winners).map(([location, locationWinners]) => (
                <div key={location} className="bg-green-100 p-4 rounded">
                  <h3 className="text-xl font-semibold mb-2">{location}</h3>
                  <ul className="list-disc pl-5">
                    {locationWinners.map((winner, index) => (
                      <li key={index} className="mb-1">
                        {winner[3]} - {winner[2]}
                      </li>
                    ))}
                  </ul>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default RandomWinnerSelector;
